// Configuration
const OWNER = 'Artem'; // Change this to your name

// Calculate lookback time (4 hours for overlap)
const lookbackHours = 4;
const lookbackDate = new Date();
lookbackDate.setHours(lookbackDate.getHours() - lookbackHours);
const afterTimestamp = Math.floor(lookbackDate.getTime() / 1000);

return {
  owner: OWNER,
  lookbackHours,
  afterTimestamp,
  afterDate: lookbackDate.toISOString()
};

// Filter emails that are likely order/delivery related
// This is a LIGHTWEIGHT PRE-FILTER - Claude does the real classification
const emails = $input.all();
const config = $('Config').first().json;

// Keywords in subject/snippet that indicate order/delivery emails (EN + DE)
// NOTE: Emails from unknown senders WILL pass if they match these keywords
const ORDER_KEYWORDS = [
  // English - Order lifecycle
  'order confirmation', 'order #', 'your order', 'order placed', 'order received',
  'shipped', 'shipping', 'has shipped', 'tracking', 'track your', 'track package',
  'delivery', 'delivered', 'out for delivery', 'arriving', 'dispatched', 'dispatch',
  'package', 'parcel', 'shipment', 'courier',
  // German - Order lifecycle  
  'bestellbestÃ¤tigung', 'bestellung', 'bestellt', 'deine bestellung',
  'versandt', 'versand', 'sendungsverfolgung', 'verfolgen',
  'lieferung', 'zustellung', 'zugestellt', 'unterwegs', 'abholung',
  'paket', 'sendung', 'deine sendung', 'ihr paket'
];

// KNOWN SENDERS - these bypass keyword check as a safety net
// This list is NOT exhaustive - smaller shops are caught via ORDER_KEYWORDS above
// Purpose: Catch carrier emails with generic subjects like 'Deine Sendung'
const KNOWN_SENDERS = [
  // Major retailers
  'amazon', 'zalando', 'asos', 'aboutyou', 'otto', 'hm.com', 'zara',
  'ebay', 'etsy', 'aliexpress', 'mediamarkt', 'saturn', 'ikea',
  // Carriers (critical - their subjects are often generic)
  'dhl', 'dpd', 'hermes', 'myhermes', 'ups', 'gls', 'fedex',
  'post.ch', 'swisspost', 'deutschepost', 'laposte',
  // Generic patterns that indicate delivery-related
  'noreply', 'no-reply', 'pakete', 'delivery', 'shipping', 'logistics',
  // Known stores from user's emails
  'gorewear', 'massimo', 'massimodutti', 'arys'
];

const results = [];

for (const item of emails) {
  const email = item.json;
  const subject = (email.Subject || email.subject || '').toLowerCase();
  const from = (email.From || email.from || '').toLowerCase();
  const snippet = (email.snippet || '').toLowerCase();

  const matchesKeyword = ORDER_KEYWORDS.some(kw => 
    subject.includes(kw) || snippet.includes(kw)
  );
  const matchesSender = KNOWN_SENDERS.some(s => from.includes(s));

  // OR logic: keyword match OR known sender
  if (matchesKeyword || matchesSender) {
    const dateMs = parseInt(email.internalDate) || Date.now();
    const dateISO = new Date(dateMs).toISOString();

    results.push({
      json: {
        id: email.id,
        threadId: email.threadId,
        subject: email.Subject || email.subject || '',
        from: email.From || email.from || '',
        date: dateISO,
        snippet: email.snippet || '',
        textPlain: email.textPlain || email.snippet || '',
        owner: config.owner
      }
    });
  }
}

if (results.length === 0) {
  return [{ json: { noEmails: true, message: 'No order-related emails found' } }];
}

return results;

// Prepare Claude API request body with properly escaped content
const items = $input.all();
const results = [];

for (const item of items) {
  const email = item.json;

  const prompt = `You are Delivery Guy, an email analysis assistant for parcel tracking. Analyze this email and extract order/delivery information if present.

Email Subject: ${email.subject || 'N/A'}
From: ${email.from || 'N/A'}
Date: ${email.date || 'N/A'}
Content: ${email.textPlain || email.snippet || 'N/A'}

Respond ONLY with valid JSON (no markdown, no explanation):
{
  "is_order_related": true or false,
  "email_type": "order_confirmation|shipping_notification|delivery_update|delivered|return|tracking_info|promotional|other",
  "order_id": "order/reference number or null",
  "store": "store/shop name or null",
  "item_description": "brief description of items (max 100 chars) or null",
  "amount": number or null,
  "currency": "EUR" or detected currency,
  "status": "ordered|confirmed|shipped|out_for_delivery|delivered|returned|unknown",
  "delivery_provider": "DHL|DPD|Hermes|UPS|GLS|FedEx|Amazon Logistics|null",
  "tracking_number": "tracking number or null",
  "tracking_url": "full tracking URL or null",
  "store_order_url": "URL to order status page on store's website or null",
  "expected_delivery": "YYYY-MM-DD or null",
  "notes": "important details or null"
}

If not order-related (promotional, newsletter, etc), set is_order_related: false.`;

  const requestBody = {
    model: 'claude-sonnet-4-20250514',
    max_tokens: 1500,
    messages: [
      {
        role: 'user',
        content: prompt
      }
    ]
  };

  results.push({ json: { requestBody, emailData: email } });
}

return results;

// Parse Claude responses - process ALL items
const allResponses = $input.all();
const allPrepared = $('Prepare Claude Request').all();
const results = [];

for (let i = 0; i < allResponses.length; i++) {
  const response = allResponses[i].json;
  const emailData = allPrepared[i]?.json?.emailData || {};

  let analysis;
  try {
    const content = response.content?.[0]?.text || '{}';
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    analysis = jsonMatch ? JSON.parse(jsonMatch[0]) : { is_order_related: false };
  } catch (e) {
    analysis = { is_order_related: false, error: e.message };
  }

  if (!analysis.is_order_related) {
    results.push({ json: { skip: true, reason: analysis.email_type || 'not_order_related', emailId: emailData.id } });
    continue;
  }

  // Generate unique order key based on store + order_id (or timestamp if no order_id)
  const storeName = (analysis.store || 'unknown').toLowerCase().replace(/[^a-z0-9]/g, '');
  const orderKey = analysis.order_id 
    ? `${storeName}_${analysis.order_id.replace(/[^a-z0-9]/gi, '')}`
    : `${storeName}_${emailData.id || Date.now()}`;

  const now = new Date().toISOString();
  const emailDate = emailData.date ? new Date(emailData.date).toISOString().split('T')[0] : now.split('T')[0];

  // Extract email from "Name <email@domain.com>" format
  const emailMatch = (emailData.from || '').match(/<([^>]+)>/);
  const emailAccount = emailMatch ? emailMatch[1] : emailData.from || '';

  results.push({
    json: {
      skip: false,
      orderKey,
      analysis,
      emailData: {
        id: emailData.id,
        subject: emailData.subject,
        from: emailData.from,
        date: emailData.date,
        owner: emailData.owner
      },
      rowData: {
        order_key: orderKey,
        order_id: analysis.order_id || '',
        owner: emailData.owner || 'Unknown',
        email_account: emailAccount,
        store: analysis.store || 'Unknown',
        item_description: (analysis.item_description || '').substring(0, 100),
        order_date: analysis.email_type === 'order_confirmation' ? emailDate : '',
        amount: analysis.amount || '',
        currency: analysis.currency || 'EUR',
        status: analysis.status || 'unknown',
        delivery_provider: analysis.delivery_provider || '',
        tracking_number: analysis.tracking_number || '',
        tracking_url: analysis.tracking_url || '',
        store_order_url: analysis.store_order_url || '',
        expected_delivery: analysis.expected_delivery || '',
        actual_delivery: analysis.status === 'delivered' ? emailDate : '',
        last_updated: now,
        last_email_subject: emailData.subject || '',
        last_email_date: emailData.date || '',
        notes: analysis.notes || ''
      }
    }
  });
}

return results;

// Decide: Create new or Update existing
// Handles multiple emails for same order in single run + tracking number matching

// Helper: Merge order data with status progression
function mergeOrderData(existing, incoming) {
  const STATUS_ORDER = ['unknown', 'ordered', 'confirmed', 'processing', 'shipped', 'in_transit', 'out_for_delivery', 'delivered', 'returned', 'cancelled'];
  const merged = { ...existing };

  // Status: only progress forward (unless returned/cancelled)
  const oldIdx = STATUS_ORDER.indexOf(existing.status || 'unknown');
  const newIdx = STATUS_ORDER.indexOf(incoming.status || 'unknown');
  if (newIdx > oldIdx || ['returned', 'cancelled'].includes(incoming.status)) {
    merged.status = incoming.status;
  }

  // Fill in missing fields (don't overwrite existing)
  const fillFields = ['tracking_number', 'tracking_url', 'store_order_url', 'delivery_provider', 'expected_delivery', 'order_id', 'amount', 'currency', 'item_description'];
  for (const field of fillFields) {
    if (!merged[field] && incoming[field]) {
      merged[field] = incoming[field];
    }
  }

  // Order date: prefer earlier (confirmation email)
  if (!merged.order_date && incoming.order_date) {
    merged.order_date = incoming.order_date;
  }

  // Actual delivery: set when status is delivered
  if (!merged.actual_delivery && incoming.actual_delivery) {
    merged.actual_delivery = incoming.actual_delivery;
  }

  // Always update these
  merged.last_updated = incoming.last_updated;
  merged.last_email_subject = incoming.last_email_subject;
  merged.last_email_date = incoming.last_email_date;

  // Append notes
  if (incoming.notes && incoming.notes !== merged.notes) {
    merged.notes = merged.notes ? merged.notes + ' | ' + incoming.notes : incoming.notes;
  }

  return merged;
}

// Build lookup maps from existing sheet data
const existingOrders = $input.all().map(item => item.json);
const existingByKey = new Map();      // order_key -> row
const existingByTracking = new Map(); // tracking_number -> order_key

existingOrders.forEach(order => {
  if (order.order_key) {
    existingByKey.set(order.order_key, order);
    // Index by tracking number for carrier email matching
    if (order.tracking_number) {
      existingByTracking.set(order.tracking_number, order.order_key);
    }
  }
});

// Get all parsed items (non-skipped)
const parsedItems = $('Parse Response').all().filter(p => !p.json.skip);

// Track pending creates to avoid duplicates within same run
const pendingCreates = new Map(); // order_key -> rowData
const results = [];

for (const item of parsedItems) {
  const parsed = item.json;
  if (!parsed || !parsed.rowData) continue;

  let orderKey = parsed.orderKey;
  let existingRow = existingByKey.get(orderKey);

  // Secondary lookup: try tracking number if no direct match
  if (!existingRow && parsed.rowData.tracking_number) {
    const keyByTracking = existingByTracking.get(parsed.rowData.tracking_number);
    if (keyByTracking) {
      orderKey = keyByTracking;
      existingRow = existingByKey.get(keyByTracking);
    }
  }

  if (existingRow) {
    // Update existing row in sheet
    const updates = mergeOrderData(existingRow, parsed.rowData);
    existingByKey.set(orderKey, updates); // Update map for subsequent emails

    // Index new tracking number if added
    if (updates.tracking_number && !existingByTracking.has(updates.tracking_number)) {
      existingByTracking.set(updates.tracking_number, orderKey);
    }

    results.push({ json: { action: 'update', orderKey: orderKey, data: updates } });

  } else if (pendingCreates.has(orderKey)) {
    // Already planning to create this order - merge instead of duplicate
    const pendingData = pendingCreates.get(orderKey);
    const merged = mergeOrderData(pendingData, parsed.rowData);
    pendingCreates.set(orderKey, merged);

    // Index tracking number
    if (merged.tracking_number) {
      existingByTracking.set(merged.tracking_number, orderKey);
    }
    // Don't push new result - we'll update the existing one

  } else {
    // Truly new order
    pendingCreates.set(orderKey, parsed.rowData);

    // Index tracking number for subsequent emails in this run
    if (parsed.rowData.tracking_number) {
      existingByTracking.set(parsed.rowData.tracking_number, orderKey);
    }

    results.push({ json: { action: 'create', orderKey: orderKey, data: parsed.rowData } });
  }
}

// Update create results with merged data (if multiple emails merged)
for (let i = 0; i < results.length; i++) {
  if (results[i].json.action === 'create') {
    const key = results[i].json.orderKey;
    results[i].json.data = pendingCreates.get(key);
  }
}

if (results.length === 0) {
  return [{ json: { action: 'none', message: 'No items to process' } }];
}

return results;
