// Configuration
const OWNER = 'Artem'; // Change this to your name
const SHEET_ID = '1H_Kr_hgdlBpl6cdZbf0ZW6cBCpw7zwCoJHoXUJ-O83Q';

return {
  owner: OWNER,
  sheetId: SHEET_ID
};

// Filter emails - lightweight pre-filter before Claude
const emails = $input.all();
const config = $('Config').first().json;

// EXCLUDED SENDERS - skip entirely (payment providers, social media, security alerts)
const EXCLUDED_SENDERS = [
  'klarna', 'paypal', 'stripe',           // Payment providers
  'newsletter', 'marketing', 'promo',      // Marketing
  'instagram', 'facebook', 'twitter', 'linkedin', 'tiktok',  // Social media
  'accounts.google', 'security@',          // Security alerts
  'lovable', 'github', 'gitlab',           // Dev tools
  'spotify', 'netflix', 'apple.com'        // Subscriptions
];

// Keywords that indicate order/delivery emails
const ORDER_KEYWORDS = [
  'order confirmation', 'order #', 'your order', 'order placed', 'order received',
  'shipped', 'shipping', 'has shipped', 'tracking', 'track your', 'track package',
  'delivery', 'delivered', 'out for delivery', 'arriving', 'dispatched', 'dispatch',
  'package', 'parcel', 'shipment', 'courier',
  'bestellbestÃ¤tigung', 'bestellung', 'bestellt', 'deine bestellung',
  'versandt', 'versand', 'sendungsverfolgung', 'verfolgen',
  'lieferung', 'zustellung', 'zugestellt', 'unterwegs', 'abholung',
  'paket', 'sendung', 'deine sendung', 'ihr paket'
];

// KNOWN SENDERS - bypass keyword check (be specific, NOT generic like 'noreply')
const KNOWN_SENDERS = [
  // Retailers
  'amazon', 'zalando', 'asos', 'aboutyou', 'otto', 'hm.com', 'zara',
  'ebay', 'etsy', 'aliexpress', 'mediamarkt', 'saturn', 'ikea',
  'gorewear', 'massimo', 'massimodutti', 'arys', 'sendcloud',
  // Carriers (important - their emails may have generic subjects)
  'dhl', 'dpd', 'hermes', 'myhermes', 'ups', 'gls', 'fedex',
  'post.ch', 'swisspost', 'deutschepost', 'laposte',
  'gls-pakete', 'paketankuendigung'
];

const results = [];

for (const item of emails) {
  const email = item.json;
  const subject = (email.Subject || email.subject || '').toLowerCase();
  const from = (email.From || email.from || '').toLowerCase();
  const snippet = (email.snippet || '').toLowerCase();

  // Skip excluded senders
  if (EXCLUDED_SENDERS.some(ex => from.includes(ex))) continue;

  const matchesKeyword = ORDER_KEYWORDS.some(kw => subject.includes(kw) || snippet.includes(kw));
  const matchesSender = KNOWN_SENDERS.some(s => from.includes(s));

  if (matchesKeyword || matchesSender) {
    const dateMs = parseInt(email.internalDate) || Date.now();
    results.push({
      json: {
        id: email.id,
        threadId: email.threadId,
        subject: email.Subject || email.subject || '',
        from: email.From || email.from || '',
        date: new Date(dateMs).toISOString(),
        snippet: email.snippet || '',
        textPlain: email.textPlain || email.snippet || '',
        owner: config.owner
      }
    });
  }
}

if (results.length === 0) {
  return [{ json: { noEmails: true, message: 'No order-related emails found' } }];
}

// Sort by date ASCENDING (oldest first) for proper chronological processing
results.sort((a, b) => new Date(a.json.date) - new Date(b.json.date));

return results;

// Merge full email body with filtered email metadata
const fullEmail = $input.first().json;
const filteredEmail = $('Loop Over Emails').first().json;

// Get text content - prefer textPlain, fallback to snippet
let textContent = fullEmail.textPlain || '';
if (!textContent && fullEmail.textHtml) {
  // Strip HTML tags as fallback
  textContent = fullEmail.textHtml.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').substring(0, 8000);
}
if (!textContent) {
  textContent = fullEmail.snippet || filteredEmail.snippet || '';
}

return {
  id: filteredEmail.id,
  threadId: filteredEmail.threadId,
  subject: filteredEmail.subject,
  from: filteredEmail.from,
  date: filteredEmail.date,
  snippet: filteredEmail.snippet,
  textPlain: textContent,
  owner: filteredEmail.owner
};

// Prepare Claude request for single email
const email = $input.first().json;

const prompt = `You are Delivery Guy, an expert email analyzer for parcel tracking. Extract ALL order/delivery information.

Email Subject: ${email.subject || 'N/A'}
From: ${email.from || 'N/A'}
Date: ${email.date || 'N/A'}
Content: ${email.textPlain || email.snippet || 'N/A'}

Respond ONLY with valid JSON:
{
  "is_order_related": true/false,
  "email_type": "order_confirmation|shipping_notification|delivery_update|out_for_delivery|delivered|invoice|payment|return|other",
  "sender_type": "shop|carrier|logistics|marketplace",
  "store": "the ACTUAL STORE/BRAND name (see rules below)",
  "mentioned_store": "store mentioned in carrier/logistics emails, or null",
  "order_ids": ["ALL order/reference numbers"],
  "tracking_numbers": ["ALL tracking numbers"],
  "item_description": "items ordered (max 100 chars)",
  "amount": number or null,
  "currency": "EUR|USD|CHF|GBP",
  "status": "ordered|confirmed|processing|shipped|in_transit|out_for_delivery|delivered|returned|unknown",
  "delivery_provider": "DHL|DPD|Hermes|UPS|GLS|FedEx|PostCH|other",
  "tracking_url": "full URL for tracking or null",
  "store_order_url": "URL to view order on store website or null",
  "expected_delivery": "YYYY-MM-DD format ONLY",
  "notes": "important details"
}

RULES:
1. STORE: For carriers (DHL, Hermes, GLS), look for 'von X', 'from X' to find actual store
2. STORE: For logistics (BARTH+CO), look for 'ex. Fa.' to find actual store
3. DATES: Always YYYY-MM-DD. Convert '30/12/25' to '2025-12-30'
4. TRACKING: DHL=20 digits, Hermes=H102.../JD000..., GLS=alphanumeric
5. URLs: Look for 'track', 'sendung', 'order-detail', 'myaccount'`;

const requestBody = {
  model: 'claude-sonnet-4-20250514',
  max_tokens: 1500,
  messages: [{ role: 'user', content: prompt }]
};

return { requestBody, emailData: email };

// Parse Claude response for single email
const response = $input.first().json;
const emailData = $('Merge Email Data').first().json;

let analysis;
try {
  const content = response.content?.[0]?.text || '{}';
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  analysis = jsonMatch ? JSON.parse(jsonMatch[0]) : { is_order_related: false };
} catch (e) {
  analysis = { is_order_related: false, error: e.message };
}

if (!analysis.is_order_related) {
  return { skip: true, reason: analysis.email_type || 'not_order_related' };
}

// Normalize arrays
const orderIds = Array.isArray(analysis.order_ids) ? analysis.order_ids : (analysis.order_id ? [analysis.order_id] : []);
const trackingNumbers = Array.isArray(analysis.tracking_numbers) ? analysis.tracking_numbers : (analysis.tracking_number ? [analysis.tracking_number] : []);

// Determine effective store
const senderType = analysis.sender_type || 'shop';
const needsMentionedStore = ['carrier', 'logistics'].includes(senderType);
const effectiveStore = needsMentionedStore 
  ? (analysis.mentioned_store || analysis.store || 'unknown')
  : (analysis.store || 'unknown');

const storeName = effectiveStore.toLowerCase().replace(/[^a-z0-9]/g, '');
const primaryOrderId = orderIds[0] || '';

// Generate order key
let orderKey;
if (primaryOrderId) {
  orderKey = `${storeName}_${String(primaryOrderId).replace(/[^a-z0-9]/gi, '')}`;
} else if (trackingNumbers[0]) {
  orderKey = `${storeName}_track_${trackingNumbers[0].replace(/[^a-z0-9]/gi, '')}`;
} else {
  orderKey = `${storeName}_${emailData.id || Date.now()}`;
}

const now = new Date().toISOString();
const emailDate = emailData.date ? new Date(emailData.date).toISOString().split('T')[0] : now.split('T')[0];

// Extract email address
const emailMatch = (emailData.from || '').match(/<([^>]+)>/);
const emailAccount = emailMatch ? emailMatch[1] : emailData.from || '';

return {
  skip: false,
  orderKey,
  senderType,
  effectiveStore,
  orderIds,
  trackingNumbers,
  mentionedStore: analysis.mentioned_store || null,
  rowData: {
    order_key: orderKey,
    order_id: orderIds.join(', '),
    owner: emailData.owner || 'Unknown',
    email_account: emailAccount,
    store: effectiveStore,
    item_description: (analysis.item_description || '').substring(0, 100),
    order_date: analysis.email_type === 'order_confirmation' ? emailDate : '',
    amount: analysis.amount || '',
    currency: analysis.currency || 'EUR',
    status: analysis.status || 'unknown',
    delivery_provider: analysis.delivery_provider || '',
    tracking_numbers: trackingNumbers.join(', '),
    tracking_url: analysis.tracking_url || '',
    store_order_url: analysis.store_order_url || '',
    expected_delivery: analysis.expected_delivery || '',
    actual_delivery: analysis.status === 'delivered' ? emailDate : '',
    last_updated: now,
    last_email_subject: emailData.subject || '',
    last_email_date: emailData.date || '',
    notes: analysis.notes || ''
  }
};

// Find matching order and decide: CREATE or UPDATE
const parsed = $('Parse Response').first().json;
const allItems = $input.all();
// Handle empty sheet - filter out empty items
const existingOrders = allItems.filter(item => item.json && Object.keys(item.json).length > 0).map(item => item.json);

// Build lookup maps
const byKey = new Map();
const byTracking = new Map();
const byStoreOrderId = new Map();

existingOrders.forEach(order => {
  if (order.order_key) {
    byKey.set(order.order_key, order);

    // Index tracking numbers
    if (order.tracking_numbers) {
      String(order.tracking_numbers).split(', ').forEach(tn => {
        if (tn) byTracking.set(tn.trim(), order.order_key);
      });
    }

    // Index store+orderId
    if (order.order_id && order.store) {
      const storeNorm = String(order.store).toLowerCase().replace(/[^a-z0-9]/g, '');
      String(order.order_id).split(', ').forEach(oid => {
        if (oid) byStoreOrderId.set(`${storeNorm}_${oid.trim()}`, order.order_key);
      });
    }
  }
});

let orderKey = parsed.orderKey;
let existingOrder = byKey.get(orderKey);
let matchMethod = existingOrder ? 'direct_key' : null;

// Level 2: Try tracking number
if (!existingOrder && parsed.trackingNumbers?.length > 0) {
  for (const tn of parsed.trackingNumbers) {
    const foundKey = byTracking.get(tn);
    if (foundKey) {
      orderKey = foundKey;
      existingOrder = byKey.get(foundKey);
      matchMethod = 'tracking_number';
      break;
    }
  }
}

// Level 3: Try mentioned_store + tracking (for carrier emails)
if (!existingOrder && ['carrier', 'logistics'].includes(parsed.senderType) && parsed.mentionedStore) {
  const mentionedNorm = parsed.mentionedStore.toLowerCase().replace(/[^a-z0-9]/g, '');
  for (const tn of (parsed.trackingNumbers || [])) {
    const foundKey = byTracking.get(tn);
    if (foundKey) {
      const order = byKey.get(foundKey);
      if (order) {
        const orderStoreNorm = String(order.store || '').toLowerCase().replace(/[^a-z0-9]/g, '');
        if (orderStoreNorm.includes(mentionedNorm) || mentionedNorm.includes(orderStoreNorm)) {
          orderKey = foundKey;
          existingOrder = order;
          matchMethod = 'carrier_store_link';
          break;
        }
      }
    }
  }
}

// Level 4: Try store + orderId
if (!existingOrder && parsed.orderIds?.length > 0) {
  const storeNorm = parsed.effectiveStore.toLowerCase().replace(/[^a-z0-9]/g, '');
  for (const oid of parsed.orderIds) {
    const lookupKey = `${storeNorm}_${String(oid).trim()}`;
    const foundKey = byStoreOrderId.get(lookupKey);
    if (foundKey) {
      orderKey = foundKey;
      existingOrder = byKey.get(foundKey);
      matchMethod = 'store_orderid';
      break;
    }
  }
}

if (existingOrder) {
  // MERGE data for UPDATE
  const STATUS_ORDER = ['unknown', 'ordered', 'confirmed', 'processing', 'shipped', 'in_transit', 'out_for_delivery', 'delivered', 'returned'];
  const merged = { ...existingOrder };
  const incoming = parsed.rowData;

  // Status: only progress forward
  const oldIdx = STATUS_ORDER.indexOf(merged.status || 'unknown');
  const newIdx = STATUS_ORDER.indexOf(incoming.status || 'unknown');
  if (newIdx > oldIdx || incoming.status === 'returned') {
    merged.status = incoming.status;
  }

  // Fill missing fields
  ['tracking_url', 'store_order_url', 'delivery_provider', 'expected_delivery', 'amount', 'currency', 'item_description'].forEach(f => {
    if (!merged[f] && incoming[f]) merged[f] = incoming[f];
  });

  // Merge tracking numbers
  const existingTN = (merged.tracking_numbers || '').split(', ').filter(t => t);
  const incomingTN = (incoming.tracking_numbers || '').split(', ').filter(t => t);
  merged.tracking_numbers = [...new Set([...existingTN, ...incomingTN])].join(', ');

  // Order date: keep existing or use incoming
  if (!merged.order_date && incoming.order_date) merged.order_date = incoming.order_date;

  // Actual delivery
  if (!merged.actual_delivery && incoming.actual_delivery) merged.actual_delivery = incoming.actual_delivery;

  // Always update
  merged.last_updated = incoming.last_updated;
  merged.last_email_subject = incoming.last_email_subject;
  merged.last_email_date = incoming.last_email_date;

  // Append notes
  if (incoming.notes && !merged.notes?.includes(incoming.notes)) {
    merged.notes = merged.notes ? merged.notes + ' | ' + incoming.notes : incoming.notes;
  }

  return { action: 'update', orderKey, matchMethod, data: merged };
} else {
  return { action: 'create', orderKey, data: parsed.rowData };
}
